#include <bits/stdc++.h>

using namespace std;

#define int ll
#define endl "\n"
#define pb push_back
#define mp make_pair
#define ff first
#define ss second
#define all(c) c.begin(), c.end()
#define vi vector<int>
#define pii pair<int,int>
#define For(i,s,e) for (ll i=(s); i<(e); i++)
#define MAX 500005

typedef long long ll;

int arr[MAX];

signed main()
{
    ios_base::sync_with_stdio(false);
    cin.tie(NULL);
    cout.tie(NULL);

    int n, k;
    cin >> n >> k;

    For(i, 1, n + 1) cin >> arr[i];

    int dp[MAX];
    dp[1] = 0;

    for (int i = 2; i <= n; i++) {
        dp[i] = 100000000000000;
        for (int j = i - 1; j >= i - k && j > 0; j--) {
            dp[i] = min(dp[i], dp[j] + abs(arr[i] - arr[j]));
        }
    }

    cout << dp[n] << endl;

    return 0;
}
