#include <bits/stdc++.h>

using namespace std;

#define int ll
#define endl "\n"
#define pb push_back
#define mp make_pair
#define ff first
#define ss second
#define all(c) c.begin(), c.end()
#define vi vector<int>
#define pii pair<int,int>
#define For(i,s,e) for (ll i=(s); i<(e); i++)
#define MAX 500005

typedef long long ll;

int arr[MAX];

signed main()
{
    ios_base::sync_with_stdio(false);
    cin.tie(NULL);
    cout.tie(NULL);

    int n, k;
    cin >> n >> k;

    For(i, 1, n + 1) cin >> arr[i];

    int dp[MAX];
    fill(dp, dp + MAX, 100000000000000LL);
    dp[1] = 0;

    deque<int> dq;
    dq.push_back(1);

    for (int i = 2; i <= n; i++) {
        while (!dq.empty() && dq.front() < i - k) {
            dq.pop_front();
        }
        dp[i] = dp[dq.front()] + abs(arr[i] - arr[dq.front()]);
        while (!dq.empty() && dp[dq.back()] >= dp[i]) {
            dq.pop_back();
        }
        dq.push_back(i);
    }

    cout << dp[n] << endl;

    return 0;
}
