#include <bits/stdc++.h>

#define maxn 1000010
#define ll long long
#define inf 0x3f3f3f3f

using namespace std;

ll dp[maxn];
ll arr[maxn];
ll n, k;

int main()
{
    memset(dp, inf, sizeof dp);
    scanf("%lld%lld", &n, &k);

    for(int i = 1; i <= n; i++)
    {
        scanf("%lld", &arr[i]);
    }

    dp[1] = 0;
    dp[0] = 0;
    dp[2] = abs(arr[2] - arr[1]);

    for(int i = 2; i <= n; i++)
    {
        for(int j = i - 1; j >= max(i - k, 1ll); j--)
            dp[i] = min(dp[i], dp[j] + abs(arr[i] - arr[j]));
    }

    printf("%lld\n", dp[n]);
    return 0;
}
