#include <bits/stdc++.h>

using namespace std;

int main() {
    int s; cin >> s;
    unordered_set<int> se;

    int steps = 1;
    se.insert(s); // Insert the initial element into the set
    while (true) {
        s = (s % 2 == 0) ? s / 2 : 3 * s + 1;
        steps++;
        if (se.find(s) != se.end()) {
            break;
        }
        se.insert(s);
    }
    cout << steps << endl;

    return 0;
}
