#include <bits/stdc++.h>

using namespace std;

int main() {
    int s; cin >> s;
    unordered_set<int> se;

    int current = s;
    int steps = 1;

    while (true) {
        if (se.find(current) != se.end()) {
            cout << steps << endl;
            return 0;
        }

        se.insert(current);

        if (current % 2 == 0) {
            current /= 2;
        } else {
            current = 3 * current + 1;
        }

        steps++;
    }
}
