#include <bits/stdc++.h>

using namespace std;

int main() {
    int s; cin >> s;
    unordered_set<int> se;

    int current = s;
    int steps = 1;

    while (se.find(current) == se.end()) {  // Continue until we find a repeated number.
        se.insert(current);

        if (current % 2 == 0) {
            current /= 2;
        } else {
            current = 3 * current + 1;
        }

        steps++;
    }

    cout << steps << endl;
    return 0;
}
