#include <bits/stdc++.h>

using namespace std;

int main() {
    int s; cin >> s;
    unordered_set<int> se;

    for (int current = s, steps = 1; ; steps++) {
        if (se.find(current) != se.end()) {
            cout << steps << endl;
            break;
        }
        se.insert(current);
        current = (current % 2 == 0) ? current / 2 : 3 * current + 1;
    }

    return 0;
}
