#include <bits/stdc++.h>

using namespace std;

int main() {
    int s; cin >> s;
    unordered_set<int> se;
    int pre = s;

    for (int steps = 1; ; steps++) {
        if (se.find(pre) != se.end()) {
            cout << steps << endl;
            break;
        }
        se.insert(pre);
        pre = (pre % 2 == 0) ? pre / 2 : 3 * pre + 1;
    }

    return 0;
}
