#include <bits/stdc++.h>

using namespace std;

int main() {
    int s; cin >> s;
    unordered_set<int> se;
    int pre = s;

    int steps = 1;
    while (se.find(pre) == se.end()) {
        se.insert(pre);
        pre = (pre % 2 == 0) ? pre / 2 : 3 * pre + 1;
        steps++;
    }
    cout << steps << endl;

    return 0;
}
