#include <bits/stdc++.h>

using namespace std;

int main() {
    int s; cin >> s;
    unordered_set<int> se;
    int pre = s;

    int steps = 1;
    se.insert(pre); // Insert the initial element into the set
    while (true) {
        pre = (pre % 2 == 0) ? pre / 2 : 3 * pre + 1;
        steps++;
        if (se.find(pre) != se.end()) {
            break;
        }
        se.insert(pre);
    }
    cout << steps << endl;

    return 0;
}
