#include <iostream>
#include <algorithm>
#include <string>
#include <iomanip>

using namespace std;

typedef long long ll;

const int maxn=1e6+1111;

int arr[maxn];
int dp[maxn];

int main(){
    int n,k;
    scanf("%d%d",&n,&k);

    // Initialize dp[0] and dp[1] directly
    dp[0]=dp[1]=0;

    // Use a large value for uninitialized dp states
    const int INF = 0x3f3f3f3f;
    for(int i=2; i<=n; ++i) {
        dp[i] = INF;
    }

    for(int i=1;i<=n;++i){
        scanf("%d",arr+i);
        for(int j=1;j<=k&&i-j>0;++j){
            dp[i]=min(dp[i-j]+abs(arr[i]-arr[i-j]),dp[i]);
        }
    }

    printf("%d\n",dp[n]);

    return 0;
}
