#include <bits/stdc++.h>

using namespace std;

typedef long long ll;

// Welcome to my source code!


int main() {
    int s;
    cin >> s;

    set<int> seen;
    seen.insert(s);

    bool flag = false;
    int ans;

    for (int i = 1; i < 1e7; i++) {
        if (s % 2 == 0) s /= 2;
        else s = 3 * s + 1;

        if (seen.find(s) != seen.end()) {
            ans = i + 1;
            flag = true;
            break;
        }

        seen.insert(s);
    }

    cout << ans << endl;
}
