#include <bits/stdc++.h>

#define ll 	long long int

#define PI 	acos(-1)

#define read 	freopen("in.txt","r",stdin)

#define uu 	first

#define vv 	second

#define write 	freopen("out.txt","w",stdout)

#define pii 	pair<int,int>

#define pll 	pair<ll,ll>

#define INF 	1e9

#define EPS 	1e-8

#define MAX 	1000006

#define MOD 	1000000007

#define fastRead 	ios_base::sync_with_stdio(0);cin.tie(0);cout.tie(0)

using namespace std;

int n ,k;

ll ara[MAX], dp[MAX];

int main() {
    fastRead;

    cin >> n >> k;

    for(int i = 0; i < n; i++) cin >> ara[i];

    dp[0] = 0; // base case

    for(int id = 1; id < n; id++) {
        dp[id] = INF;
        for(int i = 1; i <= k && id - i >= 0; i++) {
            dp[id] = min(dp[id], abs(ara[id] - ara[id-i]) + dp[id-i]);
        }
    }

    cout << dp[n-1] << endl;

    return 0;
}
