#include <iostream>
#include <cmath>
#include <algorithm>

#define ll long long int

using namespace std;

int n, k;
ll ara[1000006], dp[1000006];

int main() {
    ios_base::sync_with_stdio(0);cin.tie(0);cout.tie(0);

    cin >> n >> k;

    for(int i = 0; i < n; i++) 
        cin >> ara[i];

    dp[0] = 0; // base case

    for(int id = 1; id < n; id++) {
        dp[id] = 1e9; // Set initial value as a large number
        for(int i = 1; i <= k && id - i >= 0; i++) {
            dp[id] = min(dp[id], abs(ara[id] - ara[id-i]) + dp[id-i]);
        }
    }

    cout << dp[n-1] << endl;

    return 0;
}
