#include <bits/stdc++.h>

#define mem(x, v) memset(x, v, sizeof(x))
#define MID(a, b) ((a) + ((b) - (a)) / 2)  // using a more explicit form
#define low(x) (x & (-x))

using namespace std;

typedef long long LL;

const double EPS = 1e-10;
const int INF = 1e9 + 100;
const int N = 1e6 + 10;

int a[N], r[N];
LL fen[N];  // Fenwick Tree array
int n, tt;

inline void Add(int x, int v) {
    while (x <= 3 * n) {
        fen[x] += v;
        x += low(x);
    }
}

inline LL Query(int x) {
    LL ans = 0;
    while (x > 0) ans += fen[x], x -= low(x);
    return ans;
}

inline bool cmp(int i, int j) {
    return a[i] < a[j];
}

bool find(int k) {
    mem(fen, 0);  // Initialize Fenwick tree
    LL Ans = 0;

    vector<int> b(n + 1, 0);
    vector<int> c(a + 1, a + n + 1);

    for (int i = 1; i <= n; ++i) if (a[i] >= c[k]) b[i] = b[i - 1] + 1; else b[i] = b[i - 1];

    // r array to store the sorted indices of b for the efficient comparison
    iota(r, r + n + 1, 0);  // Initialize r with 0 to n
    sort(r, r + n + 1, cmp);  // use the inline cmp function

    // Prepare a compressed version of index
    for (int i = 0; i <= n; ++i) b[r[i]] = i + 1; // Using r for indexing b
    for (int i = 0; i <= n; ++i) {
        Ans += Query(b[i] - 1); // Use negation to get inversions effectively
        Add(b[i], 1);
    }

    LL t = (1LL * n * (n + 1)) / 2;
    return Ans >= (t + 1) / 2;
}

int main() {
    ios::sync_with_stdio(0);
    cin.tie(0);
    cout.tie(0);
    cin >> n;
    for(int i = 1; i <= n; i++) {
        cin >> a[i];
    }

    vector<int> c(a + 1, a + n + 1);  // extracting array for index calculation
    sort(c.begin(), c.end());

    int l = 0, r = n - 1, ans = 0;  // adjusted binary search bounds
    while (l <= r) {
        int mid = MID(l, r);  // using revised logic
        if (find(mid)) {
            ans = c[mid];  // set answer to current mid value
            l = mid + 1; // Proceed to find larger valid c[mid]
        } else {
            r = mid - 1;
        }
    }
    cout << ans << "\n";
    return 0;
}
