#include <iostream>
#include <vector>
#include <string>

using namespace std;

int N, M; 
bool used[1 << 18]; 
int A[1 << 18];

int main() {
    cin >> N >> M;

    int sum1 = 0, sum2 = 0;

    for (int i = 1; i <= M; i++) {
        int a;
        string b;
        cin >> a >> b;
        if (!used[a]) {
            if (b == "AC") {
                used[a] = true;
                sum1++;
                sum2 += A[a]; // Add accumulated WAs as penalties
            }
            else if (b == "WA") {
                A[a]++;
            }
        }
    }

    cout << sum1 << " " << sum2 << endl;

    return 0;
}
