#include <bits/stdc++.h>

#define INF 0x3f3f3f3f

using namespace std;

const int maxn = 1e5+5;

int h[maxn];

int d[maxn];

int main()
{
    ios::sync_with_stdio(0);
    cin.tie(0);

    int n, k;

    memset(d, INF, sizeof d);

    cin >> n >> k;

    for (int i = 1; i <= n; i++)
    {
        cin >> h[i];
    }

    d[1] = 0;
    if (n > 1) {
        d[2] = abs(h[2] - h[1]);
    }

    for (int i = 3; i <= n; i++)
    {
        for (int j = 1; j <= k && i + j <= n; j++)
        {
            d[i + j] = min(d[i + j], d[i] + abs(h[i + j] - h[i]));
        }
    }

    cout << d[n] << '\n';

    return 0;
}
