#include <bits/stdc++.h>

using namespace std;

class _in {
public:
    struct iterator {
        int x;
        explicit iterator(int _x) : x(_x) {}
        int operator*() const { return x; }
        iterator& operator++() { ++x; return *this; }
        bool operator!=(const iterator& rhs) const { return x != rhs.x; }
    };
    int l, r;
    _in(int _l, int _r) : l(_l), r(_r) {}
    iterator begin() { return iterator(l); }
    iterator end() { return iterator(r); }
};

int main() {
    cin.tie(0);
    ios::sync_with_stdio(false);

    string a, b;
    cin >> a >> b;
    int n = a.size(), m = b.size();

    vector<vector<int>> dp(n + 1, vector<int>(m + 1, 0));

    for (int i : _in(0, m + 1)) dp[0][i] = i;
    for (int i : _in(0, n + 1)) dp[i][0] = i;

    for (int i : _in(1, n + 1)) {
        for (int j : _in(1, m + 1)) {
            dp[i][j] = dp[i - 1][j - 1];
            if(a[i - 1] != b[j - 1]) dp[i][j] += 1;
            int temp = min(dp[i - 1][j] + 1, dp[i][j - 1] + 1);
            dp[i][j] = min(temp, dp[i][j]);
        }
    }

    cout << dp[n][m] << endl;

    return 0;
}
