#include <iostream>
#include <vector>
#include <string>

using namespace std;

#define FOR(i, a, n) for (ll i = (ll)a; i<(ll)n; ++i)
#define REP(i, n) FOR(i, 0, n)

typedef long long ll;
typedef vector<int> VI;

VI g[200010];
int h[100010];
ll N, K;
string s;
ll v[51];

int main(void) {
    cin >> N >> K;

    ll ans=0;
    if(K==0) {
        cout << N*N << endl;
    } else {
        for(ll i=K+1; i<=N; ++i){
            ll full_divisions = N / i;  // Calculates how many complete segments there are
            ll remaining_elements = N % i;  // Calculates how many elements are left over
            ans += full_divisions * (i - K) + max(0LL, remaining_elements - K + 1);
        }
        cout << ans << endl;
    }

    return 0;
}
