#include <bits/stdc++.h>

#define all(v) v.begin(),v.end()
#define endl '\n'
#define ll long long

double PI = acos(-1);

using namespace std;

void fast()
{
    std::ios_base::sync_with_stdio(0);
    cin.tie(0);
    cout.tie(0);
}

ll arr[10000000], n, k;
vector<ll> a(1e5 + 2, -1); // Optimized size from 1e6 to 1e5 + 2

ll minCost(ll indx)
{
    ll path1 = LLONG_MAX;

    if(indx == n-1)
        return 0;

    if(a[indx] != -1)
        return a[indx];

    for(int i = 0; i < k; i++)
    {
        if((indx + 1 + i) < n)
            path1 = min(path1, (abs(arr[indx] - arr[indx + 1 + i]) + minCost(indx + 1 + i)));
    }

    return a[indx] = path1;
}

int main()
{
    fast();

    cin >> n >> k;

    for(int i = 0; i < n; i++)
        cin >> arr[i];

    cout << minCost(0) << endl;

    return 0;
}
