#include <bits/stdc++.h>

#define all(v) v.begin(),v.end()
#define ll long long

using namespace std;

ll arr[10000000], n, k;
vector<ll> dp(1e5 + 2, -1);

ll minCost(ll indx)
{
    if(indx == n-1)
        return 0;

    if(dp[indx] != -1)
        return dp[indx];

    ll path1 = LLONG_MAX;

    for(int i = 1; i <= k; i++)
    {
        if((indx + i) < n)
            path1 = min(path1, (abs(arr[indx] - arr[indx + i]) + minCost(indx + i)));
    }

    return dp[indx] = path1;
}

int main()
{
    std::ios_base::sync_with_stdio(false);
    cin.tie(NULL);
    cout.tie(NULL);

    cin >> n >> k;

    for(int i = 0; i < n; i++)
        cin >> arr[i];

    cout << minCost(0) << endl;

    return 0;
}
