#include <bits/stdc++.h>

#define all(v) v.begin(),v.end()
#define ll long long

using namespace std;

ll arr[10000000];
int n, k;
vector<int> dp(1e5 + 2, INT_MAX);

int ans()
{
    dp[n-1] = 0;
    for(int indx = n-2; indx >= 0; indx--) {
        for(int i = 1; i <= k && (indx + i) < n; i++) {
            dp[indx] = min(dp[indx], (abs(arr[indx] - arr[indx + i]) + dp[indx + i]));
        }
    }
    return dp[0];
}

int main()
{
    std::ios_base::sync_with_stdio(false);
    cin.tie(NULL);
    cout.tie(NULL);

    cin >> n >> k;

    for(int i = 0; i < n; i++)
        cin >> arr[i];

    cout << ans() << endl;

    return 0;
}
