#include <algorithm>
#include <iostream>
#include <cstring>
#include <cstdio>
#include <cmath>
#include <vector>

#define mem(u, v) memset(u, v, sizeof(u))
#define INF 0x3f3f3f3f
#define LL long long
#define mid (l + r) / 2
#define lowbit(x) x & -x

using namespace std;

const int MAXN = 1e6 + 7;

struct Node {
    LL value;
    int position;
    bool operator<(const Node &other) const {
        return value < other.value;
    }
};

Node a[MAXN];
int c[MAXN], n;

void update(int x, int delta) {
    while (x <= 5 * n) {
        c[x] += delta;
        x += lowbit(x);
    }
}

int getsum(int x) {
    int y = 0;
    while (x > 0) {
        y += c[x];
        x -= lowbit(x);
    }
    return y;
}

bool check(LL x, LL tot) { // Added tot as parameter
    vector<int> sumIndexes(n + 1);
    int sum = 0;
    mem(c, 0);
    for (int i = 1; i <= n; ++i) {
        sum += (a[i].value >= x ? 1 : -1);
        sumIndexes[i] = sum + n + 1;
    }
    sumIndexes[0] = n + 1;
    LL ans1 = 0;
    for (int i = 0; i <= n; ++i) {
        ans1 += getsum(sumIndexes[i]);
        update(sumIndexes[i], 1);
    }
    return ans1 >= tot;
}

int main() {
    scanf("%d", &n);
    for (int i = 1; i <= n; ++i) {
        scanf("%lld", &a[i].value);
        a[i].position = i;
    }

    LL l = a[1].value, r = a[1].value;
    for (int i = 2; i <= n; ++i) {
        if (a[i].value < l) l = a[i].value;
        if (a[i].value > r) r = a[i].value;
    }

    LL tot = (1LL * n * (n + 1)) / 2; // Calculate tot for comparison

    LL ans = l;
    while (l <= r) {
        LL m = mid;
        if (check(m, tot)) ans = m, l = m + 1;
        else r = m - 1;
    }

    printf("%lld\n", ans);

    return 0;
}
