#include "bits/stdc++.h"

#define ll long long

#define PI 3.1415926535897932384626433832795l

#define MAX_NUM 1000000000

using namespace std;

ll h[1000000];
ll dp[1000000];
ll n, k;

int main()
{
    cin >> n >> k;
    for (int i = 0; i < n; i++)
    {
        cin >> h[i];
    }

    dp[n - 1] = 0;
    for (int i = n - 2; i >= 0; i--)
    {
        ll minimum = MAX_NUM;
        for (int j = 1; j <= k && i + j < n; j++)
        {
            ll temp = fabs(h[i] - h[i + j]) + dp[i + j];
            minimum = min(minimum, temp);
        }
        dp[i] = minimum;
    }

    printf("%lld\n", dp[0]);
}
