#include "bits/stdc++.h"

#define ll long long

#define MAX_NUM 1000000000

using namespace std;

ll h[1000000];
ll dp[1000000];
ll n, k;

int main()
{
    cin >> n >> k;
    for (int i = 0; i < n; i++)
    {
        cin >> h[i];
    }

    dp[n - 1] = 0;
    for (int i = n - 2; i >= 0; i--)
    {
        dp[i] = MAX_NUM;
        for (int j = 1; j <= k && i + j < n; j++)
        {
            ll cost = fabs(h[i] - h[i + j]) + dp[i + j];
            if (cost < dp[i])
            {
                dp[i] = cost;
            }
        }
    }

    printf("%lld\n", dp[0]);
}
