#include <iostream>
#include <vector>
#include <algorithm>
using namespace std;

int xg(int a) {
    return (a > 0) ? a : -a;
}

int main() {
    int n, k;
    cin >> n >> k;
    vector<int> f(k + 1, 0x3f3f3f3f);
    vector<int> w(n);

f[0] = 0;
    for (int i = 0; i < n; i++) {
        cin >> w[i];
    }
    for (int i = 1; i < n; i++) {
        for (int j = 1; j <= k; j++) {
            if (i - j < 0) break;
            f[i] = min(f[i], f[i - j] + xg(w[i - j] - w[i]));
        }
    }
    cout << (f[n - 1] == 0x3f3f3f3f ? 0 : f[n - 1]);
}
