#include <iostream> // cout, endl, cin

#include <string> // string, to_string, stoi

#include <set> // set

using namespace std;

typedef long long ll;

int calc(int a) {
    if (a % 2 == 0) return a / 2;
    else return 3 * a + 1;
}

int main() {
    int s;
    cin >> s;

    set<int> st;
    st.insert(s);

    int ans = -1;
    for (int i = 1; i < 1000000; i++) {
        s = calc(s);
        if (st.find(s) != st.end()) {
            cout << i + 1 << endl;
            return 0;
        }
        st.insert(s);
    }
    return 0;
}
