#include <iostream> // cout, endl, cin

#include <set> // set

using namespace std;

typedef long long ll;

int main() {
    int s;
    cin >> s;

    set<int> st;
    st.insert(s);

    int ans = -1;
    for (int i = 1; i < 1000000; i++) {
        if (s % 2 == 0) s = s / 2;
        else s = 3 * s + 1;

        if (st.count(s) > 0) {
            cout << i + 1 << endl;
            return 0;
        }
        st.insert(s);
    }
    return 0;
}
