#include <bits/stdc++.h>

using namespace std;

#define IO ios::sync_with_stdio(false), cin.tie(0)

typedef long long ll;

const int inf = 0x3f3f3f3f;

vector<ll> a;
vector<ll> dp;

int main()
{
    IO;
    int n, k;
    cin >> n >> k;
    a.resize(n + 1);
    dp.resize(n + 1, inf);
    for (int i = 1; i <= n; ++i) {
        cin >> a[i];
    }
    dp[1] = 0;
    for (int i = 2; i <= n; ++i) {
        for (int j = 1; j <= k && i + j <= n; ++j) {
            dp[i] = min(dp[i], dp[i - j] + abs(a[i] - a[i - j]));
        }
    }
    cout << dp[n] << endl;
}
