#include <bits/stdc++.h>

using namespace std;

#define IO ios::sync_with_stdio(false), cin.tie(0)

typedef long long ll;

const int inf = 0x3f3f3f3f;

vector<ll> a;
vector<ll> dp;

void read(vector<ll>& v) {
    for (auto& x : v) {
        cin >> x;
    }
}

void debug(const string& message) {
    cerr << message << endl;
}

void debugValue(const string& variableName, ll value) {
    cerr << variableName << " = " << value << endl;
}

int main() {
    IO;
    int n, k;
    cin >> n >> k;
    a.resize(n + 1);
    dp.assign(n + 1, inf);  // Changed to use assign for clarity
    read(a);
    dp[1] = 0;
    for (int i = 2; i <= n; ++i) {
        for (int j = 1; j <= k && i + j <= n; ++j) {
            ll currentMin = dp[i];
            dp[i] = min(dp[i], dp[i - j] + abs(a[i] - a[i - j]));
            if (dp[i] != currentMin) {
                debugValue("dp[" + to_string(i) + "]", dp[i]);
            }
        }
    }
    cout << dp[n] << endl;
}
