#include <bits/stdc++.h>

using namespace std;

typedef long long ll;

int main() {
    int s;
    cin >> s;
    set<int> used;
    used.insert(s);
    int i = 0;

    while (true) {
        if (s % 2 == 0) s /= 2;
        else s = s * 3 + 1;

        if (used.count(s)) {
            cout << i + 2 << endl; // i increments after each calculation
            return 0;
        }

        used.insert(s);
        i++;
    }
}
