#include <bits/stdc++.h>

using namespace std;

typedef long long ll;

int main() {
    int s;
    cin >> s;
    set<int> used;
    used.insert(s);

    while (used.count(s) == 0) {
        if (s % 2 == 0) s /= 2;
        else s = s * 3 + 1;

        used.insert(s);
    }

    cout << used.size() + 1 << endl; // Size of set corresponds to the number of unique steps taken
    return 0;
}
