#include <algorithm>
#include <iostream>
#include <vector>
#include <cmath>

using namespace std;

#define ll long long
#define INFL 1e18

int main(){
  int N, K;
  cin >> N >> K;
  vector<int> h(N);
  vector<double> dp(N + 10, INFL);

  for(int i = 0; i < N; i++)
    cin >> h[i];

  dp[0] = 0;

  for(int i = 0; i < N - 1; i++){
    for(int j = 1; j <= K; j++){
      if(i + j < N) {
        dp[i + j] = min(dp[i + j], dp[i] + abs(h[i + j] - h[i]));
      }
    }
  }

  cout << dp[N - 1] << endl;
}
