#include <bits/stdc++.h>

using namespace std;

#define int ll
#define endl "\n"
#define MAX 500005
#define INF LLONG_MAX

typedef long long ll;

int arr[MAX];

signed main() {
    ios_base::sync_with_stdio(false);
    cin.tie(NULL);

    int n, k;
    cin >> n >> k;

    for (ll i = 1; i <= n; i++) cin >> arr[i];

    vector<int> dp(n + 1, INF);
    dp[1] = 0;

    for (ll i = 2; i <= n; i++) {
        for (int j = 1; j <= min(i - 1, k); j++) {
            dp[i] = min(dp[i], dp[i - j] + abs(arr[i] - arr[i - j]));
        }
    }

    cout << dp[n] << endl;
    return 0;
}
