#include<bits/stdc++.h>

using namespace std;

int main(){
  int s;
  cin >> s;
  vector<bool> seen(10000, false); // Use a boolean vector to keep track

  seen[s] = true;
  for(int i=1; i<10000; i++){ // Change starting point to 1
    if(s % 2 == 0){
      s /= 2;
    }else{
      s = s * 3 + 1;
    }

    if(s < 10000) {
      if(seen[s]){ // Check if the number is already seen
        cout << i + 1 << endl; // Adjust index for output consistency
        break;
      }
      seen[s] = true; // Mark the current number as seen
    }
  }

  return 0;
}
