#include <bits/stdc++.h>

using namespace std;

int main() {
  int s; cin >> s;

  set<int> se;
  se.insert(s);

  int i = 0;
  int current = s;

  while (true) {
    if (current % 2 == 0) {
      current /= 2;
    } else {
      current = 3 * current + 1;
    }

    i++;

    if (se.count(current)) {
      cout << i + 1 << endl;
      return 0;
    }

    se.insert(current);
  }
}
