#include <bits/stdc++.h>

using namespace std;

int main() {
  int s; cin >> s;

  set<int> se;
  se.insert(s);  // Changed direct assignment to insert

  int i = 0;
  int now = s; // Using 'now' instead of 'current'
  int pre = s; // Variable to hold the previous number

  while (true) {
    if (now % 2 == 0) {
      now /= 2;
    } else {
      now = 3 * now + 1;
    }

    if (now == pre) { // Check to avoid unnecessary insert
        i++;
        cout << i + 1 << endl;
        return 0;
    }

    i++;
    pre = now; // Update previous number

    if (se.find(now) != se.end()) {
      cout << i + 1 << endl;
      return 0;
    }

    se.insert(now);
  }
}
