#include <bits/stdc++.h>

using namespace std;

int main() {
  int s; cin >> s;

  set<int> se;
  se.insert(s);

  int id = 1;  // Starting identifier from 1
  int now = s;
  int pre = s;

  while (true) {
    if (now % 2 == 0) {
      now /= 2;
    } else {
      now = 3 * now + 1;
    }

    if (now == pre) {
      cout << id + 1 << endl;
      return 0;
    }

    id++;
    pre = now;

    if (se.find(now) != se.end()) {
      cout << id + 1 << endl;
      return 0;
    }

    se.insert(now);
  }
}
