#include <bits/stdc++.h>

using namespace std;

typedef long long ll;

int main() {
    int s;
    cin >> s;

    unordered_set<int> seen;
    seen.insert(s);

    int ans = 0;

    for (int i = 1; i < 1e7; i++) {
        s = (s % 2 == 0) ? s / 2 : 3 * s + 1;

        if (seen.insert(s).second == false) {
            ans = i + 1;
            break;
        }
    }

    cout << ans << endl;
}
