#include <iostream>
#include <vector>
#include <algorithm>

#define INF 1e9
#define MAX 1000006

using namespace std;

int n, k;
int ara[MAX], dp[MAX];

int main() {
    ios_base::sync_with_stdio(0);cin.tie(0);cout.tie(0);
    cin >> n >> k;
    for (int i = 0; i < n; i++) cin >> ara[i];

    // Initialize dp array for bottom-up approach
    dp[0] = 0;
    for (int i = 1; i < n; i++) {
        dp[i] = INF;
        for (int j = 1; j <= k && i - j >= 0; j++) {
            dp[i] = min(dp[i], abs(ara[i] - ara[i - j]) + dp[i - j]);
        }
    }

    cout << dp[n - 1] << endl;
    return 0;
}
