#include <iostream>
#include <vector>
#include <algorithm>

#define INF 1e9

using namespace std;

int main() {
    int n, k;
    cin >> n >> k;
    vector<int> ara(n);
    vector<int> dp(n);

    for (int i = 0; i < n; i++) cin >> ara[i];

    // Initialize dp array for bottom-up approach
    dp[0] = 0;
    for (int i = 1; i < n; i++) {
        dp[i] = INF;
        for (int j = 1; j <= k && i - j >= 0; j++) {
            dp[i] = min(dp[i], abs(ara[i] - ara[i - j]) + dp[i - j]);
        }
    }

    cout << dp[n - 1] << endl;
    return 0;
}
