#include <iostream>
#include <vector>
#include <string>
using namespace std;

int N, M, P[1 << 18]; 
string S[1 << 18];

bool used[1 << 18]; 
int penalty[1 << 18];

int main() {
	cin >> N >> M;
	for (int i = 1; i <= M; i++) {
		cin >> P[i] >> S[i];
		if (S[i] == "AC") used[P[i]] = true;
		else if (S[i] == "WA" && !used[P[i]]) penalty[P[i]]++;
	}
	int sum1 = 0, sum2 = 0;
	for (int i = 1; i <= N; i++) {
		if (used[i]) {
			sum1++; 
			sum2 += penalty[i];
		}
	}
	cout << sum1 << " " << sum2 << endl;
	return 0;
}
