#pragma warning(disable : 4996)
#include <iostream>
using namespace std;

int main() {
    int N, M;
    cin >> N >> M;
    bool used[1 << 18] = {false};
    int penaltyCount = 0;

    for (int i = 1; i <= M; i++) {
        int a;
        string b;
        cin >> a >> b;
        if (b == "AC") used[a] = true;
        else if (b == "WA" && !used[a]) penaltyCount++;
    }
    int sum1 = 0, sum2 = 0;
    for (int i = 1; i <= N; i++) {
        if (used[i]) {
            sum1++; 
            sum2 += penaltyCount;
        }
    }
    cout << sum1 << " " << sum2 << endl;
    return 0;
}
