#include <bits/stdc++.h>

#define INF 0x3f3f3f3f

using namespace std;

const int maxn = 1e6+10;

int a[maxn];

int dp[maxn];

int main() {
    int n, k;
    memset(dp, INF, sizeof dp);
    cin.tie(0); // untie cin from cout for faster I/O
    ios::sync_with_stdio(0); // synchronize C++ streams with C streams
    cin >> n >> k;

    for (int i = 1; i <= n; i++) {
        cin >> a[i];
    }

    dp[1] = 0;
    for (int i = 1; i <= n - 1; i++) {
        int curr_val = dp[i];
        for (int j = 1; j <= k && i + j <= n; j++) {
            dp[i + j] = min(dp[i + j], curr_val + abs(a[i + j] - a[i]));
        }
    }

    cout << dp[n] << '\n';
    return 0;
}
