#include <bits/stdc++.h>

using namespace std;

class _in {
public:
    _in(int* arr, int size) : arr(arr), size(size), index(0) {}

    bool hasNext() { return index < size; }
    int next() { return arr[index++]; }

private:
    int* arr;
    int size;
    int index;
};

int main() {
    string a, b;
    cin >> a >> b;

    int n = a.size(), m = b.size();

    vector<vector<int>> dp(n + 1, vector<int>(m + 1));

    for (int i = 0; i <= m; ++i) dp[0][i] = i;
    for (int i = 0; i <= n; ++i) dp[i][0] = i;

    for (int i = 1; i <= n; ++i) {
        for (int j = 1; j <= m; ++j) {
            dp[i][j] = dp[i - 1][j - 1];

            if (a[i - 1] != b[j - 1]) dp[i][j] += 1;

            int temp = min(dp[i - 1][j] + 1, dp[i][j - 1] + 1);

            dp[i][j] = min(temp, dp[i][j]);
        }
    }

    cout << dp[n][m] << endl;

    return 0;
}
