#include <iostream>

using namespace std;

const constexpr ll MOD = 10000000000 + 7;

ll N, K;

int main(void) {
    cin >> N >> K;
    ll cnt = 0;
    if (K == 0) cout << N * N << endl;
    else {
        for (ll i = K + 1; i <= N; ++i) {
            cnt += N / i * max(ll(0), i - K) + max(ll(0), N % i - K + 1);
        }
        cout << cnt << endl;
    }
    return 0;
}
