#include <bits/stdc++.h>

#define ll long long
#define MAX_NUM 1000000000

using namespace std;

ll h[1000000];
ll dp[1000000];

int main() {
    ll n, k;
    cin >> n >> k;
    for (int i = 0; i < n; i++) {
        cin >> h[i];
    }

    memset(dp, -1, sizeof(dp[0]) * 1000000);
    dp[n - 1] = 0; // Base case initialization

    for (int i = n - 2; i >= 0; i--) {
        ll minimum = MAX_NUM;
        for (int j = 1; j <= k && i + j < n; j++) {
            // Calculate minimum cost directly using dp table
            minimum = min(minimum, abs(h[i] - h[i + j]) + dp[i + j]);
        }
        dp[i] = minimum;
    }

    printf("%lld\n", dp[0]);
}
