using namespace std;

int main() {
    int k, x;
    cin >> k >> x;
    int begin = x - (k - 1);
    int end = x + (k - 1);

    for (int i = begin; i <= end; ++i) {
        cout << i;
        if (i < end) cout << ' ';  // Avoid extra space at the end
    }
    cout << '\n';  // Newline after the output
    return 0;
}
