#include<iostream>
#include<cmath>
#include<cstdio>
#include<cstring>
#include<climits>

using namespace std;

int n, k, f[1100000], w[1100000];

int main()
{
    cin >> n >> k;

    for (int i = 0; i < n; i++) {
        f[i] = INT_MAX;
    }

    for (int i = 0; i < n; i++)
    {
        cin >> w[i];
    }

    f[0] = 0;

    for (int i = 1; i < n; i++)
    {
        for (int j = 1; j <= k; j++)
        {
            if (i - j < 0) continue;
            f[i] = min(f[i], f[i - j] + abs(w[i - j] - w[i]));
        }
    }

    cout << f[n - 1];
}
