#include <iostream> // cout, endl, cin

#include <set> // set

using namespace std;

#define rep(i,n) for (int i = 0; i < (n); ++i)

typedef long long ll;

int main(){

  int s;
  cin >> s;

  set<int> st;
  st.insert(s);

  int ans = -1;
  int current = s;
  for(int i = 1; i < 1000000; i++){
    if(current % 2 == 0) {
      current /= 2;
    } else {
      current = 3 * current + 1;
    }

    if(st.find(current) != st.end()){
      cout << i+1 << endl;
      return 0;
    }
    st.insert(current);
  }

  return 0;
}
